package day5;

public class EvenOdd {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		for(int i=1; i<=20; i++)
		{			
			if(i%2==0) 
			{
				System.out.println(i +" is even number");
			}
			else 
			{
				System.out.println(i +" is odd number");
			}
		}
	}

}
